mapper.select:
SELECT guacamole_connection_history.connection_id, guacamole_connection.connection_name, guacamole_connection_history.user_id, guacamole_user.username, guacamole_connection_history.start_date, guacamole_connection_history.end_date FROM guacamole_connection_history LEFT JOIN guacamole_connection ON guacamole_connection_history.connection_id = guacamole_connection.connection_id LEFT JOIN guacamole_user ON guacamole_connection_history.user_id = guacamole_user.user_id
(
guacamole_connection_history.user_id IN ( SELECT user_id FROM guacamole_user WHERE POSITION(#{term.term,jdbcType=VARCHAR} IN username) > 0 )
OR guacamole_connection_history.connection_id IN ( SELECT connection_id FROM guacamole_connection WHERE POSITION(#{term.term,jdbcType=VARCHAR} IN connection_name) > 0 )
OR start_date BETWEEN #{term.startDate,jdbcType=TIMESTAMP} AND #{term.endDate,jdbcType=TIMESTAMP}
)
guacamole_connection_history.start_date 1
DESC
LIMIT #{limit,jdbcType=INTEGER}
