guacamole_user_history.username = #{username,jdbcType=VARCHAR}
(
guacamole_user_history.user_id IN ( SELECT user_id FROM guacamole_user JOIN guacamole_entity ON guacamole_user.entity_id = guacamole_entity.entity_id WHERE POSITION(#{term.term,jdbcType=VARCHAR} IN guacamole_entity.name) > 0 AND guacamole_entity.type = 'USER'::guacamole_entity_type), )
OR start_date BETWEEN #{term.startDate,jdbcType=TIMESTAMP} AND #{term.endDate,jdbcType=TIMESTAMP}
)
guacamole_user_history.start_date 1
DESC
LIMIT #{limit,jdbcType=INTEGER}

