SELECT guacamole_connection.connection_id, connection_name, parent_id, protocol, max_connections, max_connections_per_user, proxy_hostname, proxy_port, proxy_encryption_method, connection_weight, failover_only FROM guacamole_connection JOIN guacamole_connection_permission ON guacamole_connection_permission.connection_id = guacamole_connection.connection_id WHERE guacamole_connection.connection_id IN
#{identifier,jdbcType=INTEGER}
AND user_id = #{user.objectID,jdbcType=INTEGER} AND permission = 'READ';

SELECT guacamole_sharing_profile.sharing_profile_id
FROM guacamole_sharing_profile
JOIN guacamole_sharing_profile_permission ON guacamole_sharing_profile_permission.sharing_profile_id = guacamole_sharing_profile.sharing_profile_id
WHERE primary_connection_id = #{connection_id}
    AND user_id = #{user.objectID,jdbcType=INTEGER}
    AND permission = 'READ';
