mapper.select:
SELECT connection_group_id
FROM guacamole_connection_group
WHERE parent_id = #{parent_id};

SELECT connection_id
FROM guacamole_connection
WHERE parent_id = #{parent_id};

SELECT guacamole_connection_group.connection_group_id, connection_group_name, parent_id, type, max_connections, max_connections_per_user, enable_session_affinity FROM guacamole_connection_group JOIN guacamole_connection_group_permission ON guacamole_connection_group_permission.connection_group_id = guacamole_connection_group.connection_group_id WHERE guacamole_connection_group.connection_group_id IN
#{identifier,jdbcType=VARCHAR}
AND user_id = #{user.objectID,jdbcType=INTEGER} AND permission = 'READ';

SELECT guacamole_connection_group.connection_group_id
FROM guacamole_connection_group
JOIN guacamole_connection_group_permission ON guacamole_connection_group_permission.connection_group_id = guacamole_connection_group.connection_group_id
WHERE parent_id = #{parent_id}
    AND user_id = #{user.objectID,jdbcType=INTEGER}
    AND permission = 'READ';

SELECT parent_id, guacamole_connection.connection_id
FROM guacamole_connection
JOIN guacamole_connection_permission ON guacamole_connection_permission.connection_id = guacamole_connection.connection_id
WHERE parent_id = #{parent_id}
    AND user_id = #{user.objectID,jdbcType=INTEGER}
    AND permission = 'READ';
