    user_id = #{user.objectID,jdbcType=INTEGER}
    AND connection_id IN

            #{identifier,jdbcType=INTEGER}

    AND permission IN

            #{permission,jdbcType=VARCHAR}

mapper.insert:
INSERT INTO guacamole_connection_permission ( user_id, permission, connection_id ) SELECT DISTINCT permissions.user_id, permissions.permission, permissions.connection_id FROM
SELECT #{permission.userID,jdbcType=INTEGER} AS user_id, #{permission.type,jdbcType=VARCHAR} AS permission, #{permission.objectIdentifier,jdbcType=INTEGER} AS connection_id
AS permissions WHERE (user_id, permission, connection_id) NOT IN ( SELECT guacamole_connection_permission.user_id, guacamole_connection_permission.permission, guacamole_connection_permission.connection_id FROM guacamole_connection_permission );
