mapper.insert:
INSERT INTO guacamole_sharing_profile_permission ( user_id, permission, sharing_profile_id ) SELECT DISTINCT permissions.user_id, permissions.permission, permissions.sharing_profile_id FROM
SELECT #{permission.userID,jdbcType=INTEGER} AS user_id, #{permission.type,jdbcType=VARCHAR} AS permission, #{permission.objectIdentifier,jdbcType=INTEGER} AS sharing_profile_id
AS permissions WHERE (user_id, permission, sharing_profile_id) NOT IN ( SELECT guacamole_sharing_profile_permission.user_id, guacamole_sharing_profile_permission.permission, guacamole_sharing_profile_permission.sharing_profile_id FROM guacamole_sharing_profile_permission );
