mapper.select:
SELECT DISTINCT connection_group_id 
FROM [guacamole_connection_group_permission]
WHERE
    user_id = #{user.objectID,jdbcType=INTEGER}
    AND connection_group_id IN

            #{identifier,jdbcType=INTEGER}

    AND permission IN

            #{permission,jdbcType=VARCHAR}

mapper.insert:
INSERT INTO [guacamole_connection_group_permission] ( user_id, permission, connection_group_id ) SELECT DISTINCT permissions.user_id, permissions.permission, permissions.connection_group_id FROM
SELECT #{permission.userID,jdbcType=INTEGER} AS user_id, #{permission.type,jdbcType=VARCHAR} AS permission, #{permission.objectIdentifier,jdbcType=INTEGER} AS connection_group_id
AS permissions WHERE NOT EXISTS (SELECT 1 FROM [guacamole_connection_group_permission] WHERE [guacamole_connection_group_permission].user_id = permissions.user_id AND [guacamole_connection_group_permission].permission = permissions.permission AND [guacamole_connection_group_permission].connection_group_id = permissions.connection_group_id );
