mapper.insert:
INSERT INTO [guacamole_user_group_permission] ( entity_id, permission, affected_user_group_id ) SELECT DISTINCT permissions.entity_id, permissions.permission, affected_group.user_group_id FROM
SELECT #{permission.entityID,jdbcType=INTEGER} AS entity_id, #{permission.type,jdbcType=VARCHAR} AS permission, #{permission.objectIdentifier,jdbcType=VARCHAR} AS affected_name
AS permissions JOIN [guacamole_entity] affected_entity ON affected_entity.name = permissions.affected_name AND affected_entity.type = 'USER_GROUP' JOIN [guacamole_user_group] affected_group ON affected_group.entity_id = affected_entity.entity_id WHERE NOT EXISTS (SELECT 1 FROM [guacamole_user_group_permission] WHERE [guacamole_user_group_permission].entity_id = permissions.entity_id AND [guacamole_user_group_permission].permission = permissions.permission AND [guacamole_user_group_permission].affected_user_group_id = affected_group.user_group_id );
